//Name: CamelCase to Proper Case
//Tooltip: Create properly formatted names. If used with PBIX than it only works with measures
//Enable: Table, Column, Measure, Hierarchy, Hierarchy Level, Perspective

foreach(var obj in Selected.OfType<ITabularNamedObject>()) {
    var oldName = obj.Name;
    var newName = new System.Text.StringBuilder();
    for(int i = 0; i < oldName.Length; i++) {
        // First letter should always be capitalized:
        if(i == 0) newName.Append(Char.ToUpper(oldName[i]));

        // A sequence of two uppercase letters followed by a lowercase letter should have a space inserted
        // after the first letter:
        else if(i + 2 < oldName.Length && char.IsLower(oldName[i + 2]) && char.IsUpper(oldName[i + 1]) && char.IsUpper(oldName[i]))
        {
            newName.Append(oldName[i]);
            newName.Append(" ");
        }

        // All other sequences of a lowercase letter followed by an uppercase letter, should have a space
        // inserted after the first letter:
        else if(i + 1 < oldName.Length && char.IsLower(oldName[i]) && char.IsUpper(oldName[i+1]))
        {
            newName.Append(oldName[i]);
            newName.Append(" ");
        }
        else
        {
            newName.Append(oldName[i]);
        }
    }
    obj.Name = newName.ToString();
}

//Name: Create %
//Tooltip: Creates a % calculation based on the selected measures. (Order of selection matters)
//Enable: Measure

var m = Selected.Measures;
if (m.Count() == 2)
{
    var first = m.First();
    var last = m.Last();
    var newMeasure = first.Table.AddMeasure(
        "% " + first.Name,                                                                  // Name
        "DIVIDE(" + first.DaxObjectFullName + "," + last.DaxObjectFullName + ")",           // DAX expression
        m.First().DisplayFolder                                                             // Display Folder   
        );
        // Set the format string on the new measure:
    newMeasure.FormatString = "0.00%;-0.00%;0.00%";

        // Provide some documentation:
    newMeasure.Description = "Expression: " +newMeasure.Expression;
};
if (m.Count() != 2)
{    
    Output("Select 2 Measures to perform the action");
}

//Name: Create AVG
//Tooltip: Creates a AVG measure for every currently selected column and hide the column.
//Enable: Column
 
foreach(var c in Selected.Columns)
{
    c.Table.AddMeasure(
        "Avg of " + c.Name,                    // Name
        "AVERAGE(" + c.DaxObjectFullName + ")",    // DAX expression
        c.DisplayFolder                        // Display Folder
    );
    c.IsHidden = true;
}

//Name: Create Base Column folders
//Tooltip: Puts columns into an other columns folder. ID columns in an id folder and hides them.
//Enable: Model
 
foreach(var c in Model.AllColumns)
{
    c.DisplayFolder = "Other Columns";
    
    if(c.Name.ToLower().Contains("id"))
    {
        c.DisplayFolder = "IDs";
        c.IsHidden = true;
    }
   
}

//Name: Create SUM
//Tooltip: Creates a SUM measure for every currently selected column and hide the column.
//Enable: Column
 
foreach(var c in Selected.Columns)
{
    c.Table.AddMeasure(
        "Sum of " + c.Name,                    // Name
        "SUM(" + c.DaxObjectFullName + ")",    // DAX expression
        c.DisplayFolder                        // Display Folder
    );
    c.IsHidden = true;
}

//Name: Default Measure Description
//Tooltip: Adds expression to the description
//Enable: Model
//Created by Mihaly Kavasi (updated by Ed Hansberry's idea)
//Tabular Editor version 2.16.0

//It is better to format DAX before adding it into the descriptions
foreach(var m in Model.AllMeasures)
{
    if(m.Description == "")
    {    
        m.Description =  "Expression:" + "\n" + m.Expression;
    }
    else if (!m.Description.Contains("Expression"))
    {
        m.Description = m.Description + "\n" + "Expression:" + "\n" + m.Expression;
    }
    else
    {
        // '2021-07-05 / B.Agullo / reset expressions already added
        int pos = m.Description.IndexOf("Expression",0); 
        bool onlyExpression = (pos == 0);
        
        if (onlyExpression) {
            m.Description = "Expression:" + "\n" + m.Expression;
        } else {
            m.Description = m.Description.Substring(0,pos-1)  + "\n" + "Expression:" + "\n" + m.Expression;
        }
    }
}

//Name: Format Measures
//Tooltip: Format all measures using the DAX Formatter API
//Enable: Model
//Created by Mihaly Kavasi
//Tabular Editor version 2.16.0

Model.AllMeasures.FormatDax();

//Name: Hide numeric Columns
//Tooltip: Hide all numeric columns from selected tables
//Enable: Table
//Created by Mihaly Kavasi
//Tabular Editor version 2.16.0

//This could be used to hide all the numeric columns from fact tables to encourage to use of explicit measures instead.
foreach(var t in Selected.Tables)
{
    foreach(var c in t.Columns)
    {
        if(c.DataType == DataType.Decimal || c.DataType == DataType.Double || c.DataType == DataType.Int64)
        {    
        c.IsHidden = true;
        }
    }
}

//Name: Hide all relationship columns
//Tooltip: Hide all columns on many side of a join
//Enable: Model
//Created by Mihaly Kavasi (originally from Matt Allington, https://exceleratorbi.com.au)
//Tabular Editor version 2.16.0

foreach (var r in Model.Relationships)
{
    var c = r.FromColumn.Name;
    var t = r.FromTable.Name;
    Model.Tables[t].Columns[c].IsHidden = true;
}

//Name: Organize ID Columns
//Tooltip: Puts ID columns in an id folder and hides them.
//Enable: Model
//Created by Mihaly Kavasi
//Tabular Editor version 2.16.0

foreach(var c in Model.AllColumns)
{
    if(c.Name.ToLower().Contains("id"))
    {
        c.DisplayFolder = "_IDs";
        c.IsHidden = true;
    }
   
}

//Name: Time intelligence
//Tooltip: Creates time intelligence measures for every selected measure. (YTD, PY, YoY, YoY%, QTD, MTD)
//Enable: Measure

var dateColumn = "'Calendar'[Dates]";
var displayFolder = "Time Intelligence";

foreach(var m in Selected.Measures) {
    // Year-to-date:
    m.Table.AddMeasure(
        m.Name + " YTD",                                       // Name
        "TOTALYTD(" + m.DaxObjectName + ", " + dateColumn + ")",     // DAX expression
        displayFolder                                      // Display Folder
    );
    
    // Previous year:
    m.Table.AddMeasure(
        m.Name + " PY",                                       // Name
        "CALCULATE(" + m.DaxObjectName + ", SAMEPERIODLASTYEAR(" + dateColumn + "))",     // DAX expression
        displayFolder                                         // Display Folder
    );    
    
    // Year-over-year
    m.Table.AddMeasure(
        m.Name + " YoY",                                       // Name
        m.DaxObjectName + " - [" + m.Name + " PY]",            // DAX expression
        displayFolder                                         // Display Folder
    );
    
    // Year-over-year %:
    m.Table.AddMeasure(
        m.Name + " YoY%",                                           // Name
        "DIVIDE(" + m.DaxObjectName + ", [" + m.Name + " YoY])",    // DAX expression
        displayFolder                                              // Display Folder
    ).FormatString = "0.0 %";  // Set format string as percentage
    
    // Quarter-to-date:
    m.Table.AddMeasure(
        m.Name + " QTD",                                            // Name
        "TOTALQTD(" + m.DaxObjectName + ", " + dateColumn + ")",    // DAX expression
        displayFolder                                             // Display Folder
    );
    
    // Month-to-date:
    m.Table.AddMeasure(
        m.Name + " MTD",                                       // Name
        "TOTALMTD(" + m.DaxObjectName + ", " + dateColumn + ")",     // DAX expression
        displayFolder                                         // Display Folder
    );
}

//Name: Set Available in MDX (on/off)
//Tooltip: Turn Available in MDX attribute on or off
//Enable: Model
//Created by Mihaly Kavasi
//Tabular Editor version 2.16.5
foreach (var c in Selected.Columns)
    if(c.IsAvailableInMDX == true)
        c.IsAvailableInMDX = false;
    else c.IsAvailableInMDX = true;
